import React, { useState } from 'react'
import './Achat.css'
import Navbar8 from '../components/navbar8'

const produitsDispo = [
  { id: 1, nom: 'Crème Hydratante', prix: 19.99 },
  { id: 2, nom: 'Sérum Éclat', prix: 29.99 },
  { id: 3, nom: 'Masque Visage', prix: 14.5 },
]

const Achat = () => {
  const [panier, setPanier] = useState([])

  const ajouterAuPanier = (produit) => {
    setPanier([...panier, produit])
  }

  const totalPanier = panier.reduce((total, item) => total + item.prix, 0)

  return (

    
    <div className="achat-pageee">
      <h1>Page d'Achat</h1>
          <Navbar8
            page4Description={
              <Fragment>
                <span className="home-text100">
                 Contactez-nous pour toute question, collaboration ou retour. Nous serions ravis de vous entendre.
                </span>
              </Fragment>
            }
            action1={
              <Fragment>
    <button onClick={handleAcheter} className="home-button">
              Acheter Maintenant
            </button>         
             </Fragment>
            }
            link2={
              <Fragment>
                <span className="home-text102">Produits</span>
              </Fragment>
            }
            page1={
              <Fragment>
                <span className="home-text103">Home</span>
              </Fragment>
            }
            link1={
              <Fragment>
                <span className="home-text104">Home</span>
              </Fragment>
            }
            page4={
              <Fragment>
                <span className="home-text105">Contact</span>
              </Fragment>
            }
            page2={
              <Fragment>
                <span className="home-text106">Produits</span>
              </Fragment>
            }
            link4={
              <Fragment>
                <span className="home-text107">Contact</span>
              </Fragment>
            }
            page1Description={
              <Fragment>
                <span className="home-text108">
                  Bienvenue sur le site de notre marque de cosmétiques. Découvrez notre gamme de produits de haute qualité.
                </span>
              </Fragment>
            }
            page2Description={
              <Fragment>
                <span className="home-text109">
                 Explorez notre collection de soins de la peau et de produits de beauté conçus pour sublimer votre beauté naturelle.
                </span>
              </Fragment>
            }
            link3={
              <Fragment>
                <span className="home-text110">A propos de nous </span>
              </Fragment>
            }
            page3={
              <Fragment>
                <span className="home-text111">A propos de nous </span>
              </Fragment>
            }
            page3Description={
              <Fragment>
                <span className="home-text112">
                  Découvrez l’histoire de notre marque, ses valeurs et son engagement pour la beauté et le développement durable.
                </span>
              </Fragment>
            }
            action2={
              <Fragment>
                <span className="home-text113">En savoir Plus</span>
              </Fragment>
            }
          ></Navbar8>
      <div className="produits-sectionnn">
        <h2>Produits disponibles</h2>
        <ul className="produits-listeee">
          {produitsDispo.map((produit) => (
            <li key={produit.id} className="produit-itemmm">
              <h3>{produit.nom}</h3>
              <p>Prix : {produit.prix.toFixed(2)} €</p>
              <button onClick={() => ajouterAuPanier(produit)} className='button'>
                Ajouter au panier
              </button>
            </li>
          ))}
        </ul>
      </div>

      <div className="panier-sectionnn">
        <h2>Votre panier</h2>
        {panier.length === 0 ? (
          <p>Panier vide.</p>
        ) : (
          <ul>
            {panier.map((item, index) => (
              <li key={index}>
                {item.nom} - {item.prix.toFixed(2)} €
              </li>
            ))}
          </ul>
        )}
        <h3>Total : {totalPanier.toFixed(2)} €</h3>
      </div>
    </div>
  )
}

export default Achat
